import axios from 'axios'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import DataTable from 'react-data-table-component';

const URI = 'http://localhost:8000/empleados/'


const CompShowEmplados = () =>{
    const [empleados, setEmpleado] = useState([])
    useEffect ( () => {
        getEmpleados()
    },[])

    const getEmpleados = async () => {
     const res = await axios.get(URI)
     setEmpleado(res.data)
 }

 const deletedEmplado = async (id) =>{
    await axios.delete(`${URI}${id}`)
    getEmpleados()
}

const columns = [
{
    name: 'COD',
    selector: row => row.nombres+' '+row.numeroEmpleado,
},
{
    name: 'Nombres',
    selector: row => row.nombres+' '+row.apellidos,
},
{
    name: 'Documento',
    selector: row => row.documento,
},
{
    name: 'Acciones',
    selector: row => <Link to={`edit-empleado/${row.id}`} className='btn btn-primary'><i className="fa-solid fa-pen"></i></Link> ,
    
},

];



return(
    <div className='container'>
    <div className='row'>
    <div className='col'>
    <div className='table'>
    <table className='table'>
    <thead className='table-primary'>
    <tr>
    <th>COD</th>
    <th>Nombre</th>
    <th>Documento</th>
    <th>Acciones</th>
    </tr>
    </thead>
    <tbody>

    {empleados.map((empleado) => (
        <tr key={empleado.id}>
        <td>{empleado.numeroEmpleado}</td>
        <td>{empleado.nombres} {empleado.apellidos}</td>
        <td>{empleado.documento}</td>
        <td>
        <Link to={`edit-empleado/${empleado.id}`} className='btn btn-primary'><i className="fa-solid fa-pen"></i></Link>
        <Link to={`edit-empleado/${empleado.id}`} className='btn btn-success'><i className="fa-solid fa-briefcase"></i></Link>
        <button onClick={()=>deletedEmplado(empleado.id)} className='btn btn-danger'><i className="fa-solid fa-trash-can"></i></button>
        </td>
        </tr>
        ))}
    </tbody>
    </table>

    <DataTable
    columns={columns}
    data={empleados}
    />

    </div>

    <div className='createEmpleado'> 
    <Link to={'/crear-empleado'} className='btn btn-primary'>AÃ±adir Empleado</Link>
    </div>

    </div>

    </div>

    </div>

    )

}

export default CompShowEmplados
