import axios from 'axios'
import { useState,useEffect } from 'react'
import { Link } from 'react-router-dom'
import Modal from 'react-bootstrap/Modal'
import { useParams } from 'react-router-dom'

const URI = 'http://localhost:8000/empleados/contratos/'
const URIE = 'http://localhost:8000/empleados/'


const cancelCourse = () => { 
	document.getElementById("create-form").reset();
}

const CompShowContratos = () => {

	const idempleado = useParams()

	const [show, setShow] = useState(false);
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);




	const [nombres, setNombres] = useState('')
	const [apellidos, setApellidos] = useState('')
	const [tipoDoc, setTipoDoc] = useState('')
	const [documento, setDocumento] = useState('')
	const [direccion, setDireccion] = useState('')
	const [numeroEmpleado, setNumeroEmpleado] = useState('')

	const [contrato,setContrato]= useState('')
	const [numeroContrato,setNumeroContrato]= useState('')
	const [fechaInicio, setFechaInicio]= useState('')
	const [fechaFinal, setFechaFinal]= useState('')

	const updateEmpleado = async (e) =>{

		e.preventDefault()

		const res =  await axios.put(URI+idempleado.id, {
			nombres:nombres,
			apellidos:apellidos,
			tipoDoc:tipoDoc,
			documento:documento,
			celular:celular,
			direccion:direccion,
			ciudad:ciudad,
			idmetodoPago:idmetodoPago,
			idsalud:idsalud,
			idpension:idpension,
			idarl:idarl,
			idcajaCompensacion:idcajaCompensacion,
			idcensantias:idcensantias,
			datosPago: datosPago,
		})

		if(res.data.type == 200){
			handleShow()
			getEmpleadoById()
		}
	}

	useEffect ( () => {
		getEmpleadoById()
		getContratoById()
	},[])

	const getEmpleadoById = async () =>{
		const res =  await axios.get(URIE+idempleado.id)
		setNombres(res.data.nombres)
		setNumeroEmpleado(res.data.numeroEmpleado)
		setApellidos(res.data.apellidos)
		setTipoDoc(res.data.tipoDoc)
		setDocumento(res.data.documento)
	}

	const getContratoById = async () =>{
		const res =  await axios.get(URI+idempleado.id)
		setContrato(res.data.contrato)
		setNumeroContrato(res.data.numeroContrato)
		setFechaInicio(res.data.fechaInicio)
		setFechaFinal(res.data.fechaFinal)
	}


	return(
		<div>
		<div className="container">
		<div className="row">
		<p>Hola</p>
		<div className="col-md-6">
	{/*{celular}*/}
	</div>


	</div>
	</div>
	</div>
	)
}

export default CompShowContratos
